import { useState ,useEffect} from 'react';
import './App.css';

import MovieCard from './MovieCard';
const API_URL = 'http://www.omdbapi.com?apikey=e85a2d6e';
function App() {

  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm]=useState('');

  const SearchMovies = async (title) => {
    const response = await fetch(`${API_URL}&s={title}`)
    const data = await response.json();
    setMovies(data.Search);

  }
  useEffect(() => {
    SearchMovies();
  },[])

  return (
    <>
      <section>
        <div className='container'>
          <div className='row'>
            <div className='col-md-4'>
              <input type="text" value={searchTerm} placeholder="search Movie" onChange={(e)=>setSearchTerm(e.target.value)} />
              <a className='' href='' onClick={()=> SearchMovies(searchTerm)}><button>Search Movie</button></a>
            </div>
          </div>
        </div>
      </section>
      {
        movies?.length > 0
          ? (
            <div className='container'>
              {movies.map((movie)=>(
                <MovieCard movie={movie}/>
              ))}
            </div>
          ) : (
            <div><h2>No movies found</h2></div>
          )
      }
    </>
  );
}



export default App;
